package codegen

import "fmt"

const codegenmarker = `/**
 * Code generated by mercury. DO NOT EDIT.
 * versions:
 * 	mercury %s
 * 	protoc   %s
 * source: %s
 */

`

func getCodeGenmarker(version, protocVersion, sourceFile string) string {
	return fmt.Sprintf(codegenmarker, version, protocVersion, sourceFile)
}
